import React from 'react';
import ReactDOM from 'react-dom';

const App = () => {
  //Main data
  const course = 'Half Stack application development'
  const part1 = {
    name: 'Fundamentals of React',
    exercises: 10
  }
  const part2 = {
    name: 'Using props to pass data',
    exercises: 7
  }
  const part3 = {
    name: 'State of a component',
    exercises: 14
  }

  //Component for Header section. Info is passed as props.
  const Header = (props) => (
        <h1>{props.course}</h1>
  )

  //Component for Part. Each part is passed as props the info that needs to be rendered from Content.
  const Part = (props) => {
      return(
        <div>
            {props.name} {props.exercises}
        </div>
      )
  }

  //Component for Content. Content renders all the Part components. Content gets all data passed as props.
  const Content = (props) => (
      <div>
            <Part name={props.part1.name} exercises={props.part1.exercises} />
            <Part name={props.part2.name} exercises={props.part2.exercises} />
            <Part name={props.part3.name} exercises={props.part3.exercises} />
      </div>
  )

  //Component for Total.
  const Total = (props) =>(
        <p>Number of exercises {props.total}</p>
  )


  return (
    <div>
      <Header course = {course}/>
      <Content
        part1 = {part1}
        part2 = {part2}
        part3 = {part3}
      />
      <Total total = {part1.exercises + part2.exercises + part3.exercises} />
    </div>
  )
}

ReactDOM.render(<App />, document.getElementById('root'));
