import React, { useState } from 'react'
import ReactDOM from 'react-dom'

const Button = (props) =>{
    //Generic button definition. Button displays name and adds score accordingly
    return(
        <button onClick={props.onClick}>{props.name}</button>
    )
}

const Score = (props) =>{
    //Generic Score component definition for displaying feedback
    return(
        <div>{props.name + ' ' + props.score}</div>
    )
}

const App = () => {
    // Save clicks of each button to own state
    const [good, setGood] = useState(0)
    const [neutral, setNeutral] = useState(0)
    const [bad, setBad] = useState(0)

    //Computes total
    let total = good+neutral+bad

    //Computes average
    let average = (good-bad)/total

    //Computes percentage
    let percentage = 100 * good/total + '%'

    return(
        <div>
            <div>
                <h1>Give feedback</h1>
                <Button name="Good" onClick={() => setGood(good + 1)} />
                <Button name="Neutral" onClick={() => setNeutral(neutral + 1)}/>
                <Button name="Bad" onClick={() => setBad(bad + 1)}/>
            </div>
            <div>
                <h1>Statistics</h1>
                <Score name="Good" score={good} />
                <Score name="Neutral" score={neutral} />
                <Score name="Bad" score={bad} />
                <Score name="All" score={total} />
                <Score name="Average" score={average} />
                <Score name="Positive" score={percentage} />
            </div>
        </div>
    )
}

ReactDOM.render(<App />,
    document.getElementById('root')
)
