import React from 'react'
import Country from './Country'
import CountryInfo from './CountryInfo'

const DisplayCountries = ({countries, search, countriesFiltered, setCountriesFiltered}) => {
    
    const renderCountries = () => {
        if(search === ''){
            return(<div>Use the search box to find info about countries.</div>)
        }else{
            const reg = new RegExp(search, "i")
            setCountriesFiltered(countries.filter(country => reg.test(country.name))) 
            
            if(countriesFiltered.length > 10){
                return(<div>Too many matches, specify your search.</div>)
            }else{
                if (countriesFiltered.length > 1){
                    return(
                        countriesFiltered.map(country => <Country key={country.name} name={country.name}/>)
                    )
                }else if (countriesFiltered.length === 1){
                    return(<CountryInfo country={countriesFiltered[0]} />)
                }else{
                    return(<div>No matches :'(</div>)
                }
            }
        }
    }

    return(
        <div>
            {renderCountries()}
        </div>
    )
}

export default DisplayCountries
