import React from 'react'
import Country from './Country'
import CountryInfo from './CountryInfo'

const DisplayCountries = ({countries, search, filteredCountries, setFilteredCountries}) => {
    
    const renderCountries = () => {
        if(search === ''){
            return(<div>Use the search box to find info about countries.</div>)
        }else{            
            if(filteredCountries.length > 10){
                return(<div>Too many matches, specify your search.</div>)
            }else{
                if (filteredCountries.length > 1){
                    return(
                        filteredCountries.map(country => 
                            <Country 
                                key={country.name} 
                                name={country.name}
                                setFilteredCountries={setFilteredCountries} 
                            />)
                    )
                }else if (filteredCountries.length === 1){
                    return(<CountryInfo country={filteredCountries[0]} />)
                }else{
                    return(<div>No matches :'(</div>)
                }
            }
        }
    }

    return(
        <div>
            {renderCountries()}
        </div>
    )
}

export default DisplayCountries
