import React from 'react'
import Country from './Country'

const DisplayCountries = ({countries, search}) => {
    
    const renderCountries = () => {
        if(search === ''){
            return(<div>Use the search box to find info about countries.</div>)
        }else{
            const reg = new RegExp(search, "i")
            const countriesFiltered = countries.filter(country => reg.test(country.name))
            console.log(countriesFiltered)
            return(
                countriesFiltered.map((country) => <Country name= {country.name} />)
            )
        }
    }

    return(
        <div>
            {renderCountries()}
        </div>
    )
}

export default DisplayCountries
