import React from 'react'


//Message component render both notification (if called with err = false) and error messages (if called with err = true)
const Message = ({message, err}) =>{
    
    if (message === null) {
        return null
    }


    if(err === false){
        const style = {
            color: "red",
            background: "lightgrey",
            fontSize: "20px",
            borderStyle: "solid",
            borderRadius: "5px",
            padding: "10px",
            marginBottom: "10px"
            }
    }else{
        const style = {
            color: "green",
            background: "lightgrey",
            fontSize: "20px",
            borderStyle: "solid",
            borderRadius: "5px",
            padding: "10px",
            marginBottom: "10px"
        }
    }
    

    return (
        <div>
            {message}
        </div>
    )
}

export default Message
