import React from 'react'


//Message component render both notification (if called with err = false) and error messages (if called with err = true)
const Message = ({message}) =>{
    
    let style = {}

    if (message.mes === null) {
        style = {}
        return null
    }

    
    if(message.err === true){
        style = {
            color: "red",
            background: "lightgrey",
            fontSize: "20px",
            borderStyle: "solid",
            borderRadius: "5px",
            padding: "10px",
            marginBottom: "10px"
            }
    }else if(message.err === false){
        style = {
            color: "green",
            background: "lightgrey",
            fontSize: "20px",
            borderStyle: "solid",
            borderRadius: "5px",
            padding: "10px",
            marginBottom: "10px"
        }
    }
    

    return (
        <div style={style}>
            {message.mes}
        </div>
    )
}

export default Message
