import React from 'react';
import contactsServices from '../services/contacts'

//Person component renders a single person.

const handleClick = (key, name) =>{
    console.log(key)
    //When clicking a window pops up to tell the user to confirm the action. If confirmed, the contact is deleted.
    if (window.confirm(`Are you sure you want to delete '${name}'?`)) {
        contactsServices
            .deleteContact(key)
    }
}

const Person = ({key, name, number}) => 
    <div> 
        {name} {number} <button onClick={() => handleClick(key, name)}>delete</button>
    </div>

export default Person
