import React, { useState } from 'react';

//COMPONENTS
const Person = ({name, number}) => <div> {name} {number}</div>

const Search = ({newSearch, handleTyping, setNewSearch}) =>{
  return(
    <div>
      Search: <input value={newSearch} onChange={(e) => handleTyping(setNewSearch, e)}/>
    </div>
  )
}

const NewName = ({newName,setNewName,newNumber,setNewNumber,handleTyping}) =>{
  return(
    <div>
      <h2>Add new</h2>
      <form>
            <div>
              name: <input value={newName} onChange={(e) => handleTyping(setNewName, e)}/>
            </div>
            <div>
              number: <input value={newNumber} onChange={(e) => handleTyping(setNewNumber, e)}/>
            </div>
            <div>
              <button type="submit" onClick={handleSend}>add</button>
            </div>
          </form>
    </div>
  )
}

const App = () => {
    //STATE:
    const [ persons, setPersons] = useState([
      { name: 'Arto Hellas',number: '040-1234567'},
      { name: 'Ada Lovelace', number: '39-44-5323523' },
      { name: 'Dan Abramov', number: '12-43-234345' },
      { name: 'Mary Poppendieck', number: '39-23-6423122'}
    ]) 
    const [ newName, setNewName ] = useState('')
    const [ newNumber, setNewNumber] = useState('')
    const [ newSearch, setNewSearch] = useState('')
    
    //HELPER FUNCTIONS:

    //handleTyping updates the state of the variable assigned to the field as the user types
    const handleTyping = (setNew,e) => {
      setNew(e.target.value)
    }

    //handleSend updates the state of person so the newName typed is added
    const handleSend = (event) => {
        event.preventDefault()

        //Prevents contacts for being stored empty
        if (newName.length * newNumber.length === 0) return

        //Prevents storing contact that already exist
        if (persons.find(person => person.name === newName)){
            window.alert(`${newName} is already added to phonebook`)
            return
        }

        //Set new person
        const personObject = {
            name: newName,
            number: newNumber
        }
        setPersons(persons.concat(personObject))

        //Restore defaults for fields
        setNewName('')
        setNewNumber('')
    }

    //renderNumbers renders all numbers if search field is empty. Renders matches if search field is being used.
    function renderNumbers() {
      
      if (newSearch === ''){
        return persons.map((person)=><Person key={person.name} name={person.name} number={person.number}/>)
      }else{
        let re = new RegExp(newSearch, 'i')
        let personsFiltered = persons.filter((person)=> re.test(person.name))
        return personsFiltered.map((person)=><Person key={person.name} name={person.name} number={person.number}/>)
      }
      
    }

    return (
      <div>
        <h2>Phonebook</h2>

        <Search 
          newSearch={newSearch} 
          setNewSearch={setNewSearch} 
          handleTyping={handleTyping}
        />
        
        <h2>Add new</h2>
        <form>
          <div>
            name: <input value={newName} onChange={(e) => handleTyping(setNewName, e)}/>
          </div>
          <div>
            number: <input value={newNumber} onChange={(e) => handleTyping(setNewNumber, e)}/>
          </div>
          <div>
            <button type="submit" onClick={handleSend}>add</button>
          </div>
        </form>
        <NewName 
          newName = {newName}
          setNewName = {setNewName}
          newNumber = {newNumber}
          setNewNumber = {setNewNumber}
          handleTyping = {handleTyping}
        /> 
        
        <h2>Numbers</h2>
        {renderNumbers()}
      
      </div>
    )
  }
  
  export default App
