import React from 'react';
import contactsServices from '../services/contacts'

//Person component renders a single person.

const handleClick = (id, name) =>{
    //When clicking a window pops up to tell the user to confirm the action. If confirmed, the contact is deleted.
    if (window.confirm(`Are you sure you want to delete '${name}'?`)) {
        contactsServices
            .deleteContact(id)
            .then(response => console.log(response))
            .catch(err => alert("Something went wrong. "+err))
            //TODO: UPDATE STATE WHEN CONTACT IS DELETED
            
    }
}

const Person = ({id, name, number}) => 
    <div> 
        {name} {number} <button onClick={() => handleClick(id, name)}>delete</button>
    </div>

export default Person
